import React, { useState } from "react";
import axios from "axios";

function App() {
	const [data, setData] = useState({});
	const [location, setLocation] = useState("");

	const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=ab9d9b80bc08b3981bbf39c5976a7e90`;

	const searchLocation = (e) => {
		if (e.key === "Enter") {
			axios.get(url).then((res) => {
				setData(res.data);
				console.log(res.data);
			});
		}
	};

	return (
		<div className="app">
      <div className="search">
        <input value={location}
        type="text"></input>
      </div>
			<div className="container">
				<div className="top">
					<div className="location">
						<p>Charlotte</p>
					</div>
					<div className="temp">
						<h1>
							<span>65&#8457;</span>
						</h1>
					</div>
					<div className="description">
						<p>cloudy</p>
					</div>
				</div>
				<div className="bottom">
					<div className="feels">
						<p className="bold">
							<span>65&#8457;</span>
						</p>
						<p>Feels Like</p>
					</div>
					<div className="humidity">
						<p className="bold">20%</p>
						<p>Humidity</p>
					</div>
					<div className="wind">
						<p className="bold">12 MPH</p>
						<p>Wind Speed</p>
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
